(7029, 951, 'integedien', '<ul>\r\n 	<li>200g flour</li>\r\n 	<li>150g castor sugar</li>\r\n 	<li>30g cocoa powder</li>\r\n 	<li>2tsp baking soda</li>\r\n 	<li>175g chocolate chips</li>\r\n 	<li>2 eggs</li>\r\n 	<li>100ml vegetable oil</li>\r\n 	<li>200g curd / yoghurt</li>\r\n 	<li>50ml water</li>\r\n</ul>'),
(7030, 951, '_integedien', 'field_597ed2046dfdf'),
(7031, 951, 'method', '<ul>\r\n 	<li>Whisk together the vegetable oil, eggs, and castor sugar until combined.</li>\r\n 	<li>In a separate bowl, sift together the flour, cocoa powder, and baking soda.</li>\r\n 	<li>Add the eggs and oil to the flour and whisk till combined.</li>\r\n 	<li>Add the curd to the water and whisk.</li>\r\n 	<li>Fold into the batter.</li>\r\n 	<li>Add 125g chocolate chips and mix into the batter.</li>\r\n 	<li>Fill the muffin liners halfway.</li>\r\n 	<li>Sprinkle the remaining chocolate chips on each muffin.</li>\r\n 	<li>Bake at 165C for 20 minutes or until a skewer inserted comes out clean.</li>\r\n</ul>'),
