(7057, 953, 'integedien', '<ul>\r\n 	<li>75g dark chocolate</li>\r\n 	<li>125g butter</li>\r\n 	<li>50g brown sugar</li>\r\n 	<li>100g flour</li>\r\n 	<li>75g oats</li>\r\n 	<li>400g Nutella</li>\r\n 	<li>100g chopped roasted hazelnuts</li>\r\n</ul>'),
(7058, 953, '_integedien', 'field_597ed2046dfdf'),
(7059, 953, 'method', '<ul>\r\n 	<li>In a microwave, melt the dark chocolate and butter and leave aside to cool.</li>\r\n 	<li>Add the brown sugar and whisk till combined properly.</li>\r\n 	<li>Fold in the flour with a spatula and then add the oats and mix.</li>\r\n 	<li>Preheat the oven at 180C and either grease an 8-inch” baking tin with butter or line it with parchment paper. Bake for 15–20 minutes.</li>\r\n 	<li>Once cooled, spread the Nutella evenly on top and garnish with the chopped roasted hazelnuts.</li>\r\n</ul>'),
