(7103, 960, 'integedien', '<strong>Bottom layer</strong>\r\n<ul>\r\n 	<li>2 Granny Smith apples</li>\r\n 	<li>30g butter</li>\r\n 	<li>30g castor sugar</li>\r\n 	<li>50ml water</li>\r\n 	<li>1tsp cinnamon powder</li>\r\n</ul>\r\n<strong>Top layer</strong>\r\n<ul>\r\n 	<li>3 Granny Smith apples</li>\r\n 	<li>30g butter</li>\r\n 	<li>30g brown sugar</li>\r\n</ul>'),
(7582, 304, 'use_in_first_product', '7 inch Round Spring Form Pan'),
