(7122, 962, 'integedien', '<ul>\r\n 	<li>100g butter</li>\r\n 	<li>80g dark chocolate</li>\r\n 	<li>125g castor sugar</li>\r\n 	<li>100g water</li>\r\n 	<li>70g flour</li>\r\n 	<li>5g cocoa powder</li>\r\n 	<li>1 egg</li>\r\n 	<li>½tsp baking powder</li>\r\n</ul>'),
(7123, 962, '_integedien', 'field_597ed2046dfdf'),
(7124, 962, 'method', '<ul>\r\n 	<li>Over a double boiler, melt together the butter, dark chocolate, castor sugar and water.</li>\r\n 	<li>Once cooled properly, add the egg and mix.</li>\r\n 	<li>Sift together the flour and baking powder and fold into the chocolate mix with a spatula till mixed.</li>\r\n 	<li>Pour into the mould and bake at 180C for 12–15 minutes.</li>\r\n</ul>'),
