(7141, 964, 'integedien', '<ul>\r\n 	<li>125g cream cheese cold</li>\r\n 	<li>50g butter, at room temperature</li>\r\n 	<li>1tsp lemon zest</li>\r\n 	<li>300g icing sugar, sifted</li>\r\n</ul>'),
(7142, 964, '_integedien', 'field_597ed2046dfdf'),
(7143, 964, 'method', '<ul>\r\n 	<li>In a large bowl, whisk the cream cheese and butter until smooth.</li>\r\n 	<li>Add the lemon zest and beat well.</li>\r\n 	<li>Gradually add the icing sugar, whisking continuously until smooth and creamy.</li>\r\n 	<li>Cover and refrigerate the frosting for 2 to 3 hours, but no more before using.</li>\r\n</ul>'),
