(7162, 968, 'integedien', '<ul>\r\n 	<li>100g butter</li>\r\n 	<li>1tsp vanilla essence</li>\r\n 	<li>225g icing or powdered sugar</li>\r\n 	<li>1 tablespoon milk</li>\r\n 	<li>A few drops of liquid food colouring of your choice</li>\r\n</ul>'),
(7163, 968, '_integedien', 'field_597ed2046dfdf'),
(7164, 968, 'method', '<ul>\r\n 	<li>In a bowl, whisk together the butter and vanilla essence until smooth.</li>\r\n 	<li>Gradually add in the icing sugar and whisk till mixed. Add the milk and beat to combine.</li>\r\n 	<li>Keep whisking until the frosting is light and fluffy.</li>\r\n 	<li>It should take you around 5 minutes if using a hand whisk.</li>\r\n 	<li>If not using right away, cover it with cling wrap to prevent it from drying out.</li>\r\n</ul>'),
