(7201, 970, 'integedien', '<ul>\r\n 	<li>125g butter</li>\r\n 	<li>125g castor sugar</li>\r\n 	<li>2 eggs</li>\r\n 	<li>100g flour</li>\r\n 	<li>1tsp baking soda</li>\r\n 	<li>50g ground almonds</li>\r\n 	<li>1tsp lemon zest</li>\r\n 	<li>5tsp lemon juice</li>\r\n</ul>'),
(7202, 970, '_integedien', 'field_597ed2046dfdf'),
(7203, 970, 'method', '<ul>\r\n 	<li>Whisk the butter and castor sugar together until thick.</li>\r\n 	<li>Add the eggs one at a time and whisk well till combined.</li>\r\n 	<li>Sift the flour and baking soda together and add to the mixture.</li>\r\n 	<li>Add the almonds, lemon zest, and lemon juice and give it a final mix.</li>\r\n 	<li>Bake in a lined 250g loaf tin at 165C for 20 minutes.</li>\r\n</ul>'),
