(7220, 973, 'integedien', '<strong>Ingredients for the cake batter :</strong>\r\n<ul>\r\n 	<li>100g butter</li>\r\n 	<li>100g brown sugar</li>\r\n 	<li>2 eggs</li>\r\n 	<li>80g flour</li>\r\n 	<li>25g cocoa powder</li>\r\n 	<li>1tsp baking powder</li>\r\n 	<li>40ml milk</li>\r\n</ul>\r\n<strong> Ingredients for the chocolate ganache frosting :</strong>\r\n<ul>\r\n 	<li>180ml cream</li>\r\n 	<li>250g semisweet or bittersweet chocolate, cut into small pieces</li>\r\n 	<li>20g butter</li>\r\n</ul>'),
(7221, 973, '_integedien', 'field_597ed2046dfdf'),
(7222, 973, 'method', '<ul>\r\n 	<li>In a bowl, whisk together the butter and brown sugar until light and fluffy.</li>\r\n 	<li>Add the eggs to the mixture and whisk till combined.</li>\r\n 	<li>Sift together the flour, cocoa powder, and baking powder and fold into the mixture with a spatula.</li>\r\n 	<li>Add the milk and mix thoroughly.</li>\r\n 	<li>Line a cupcake mold with liners and scoop the batter into each liner till 2/3rd full.</li>\r\n 	<li>Bake at 175°C for 15 minutes or until a skewer when inserted in the center of the cake comes out clean.</li>\r\n 	<li>For the ganache, place the chopped chocolate in a medium-sized stainless steel bowl and set aside.</li>\r\n 	<li>Heat the cream and butter in a saucepan over medium heat.</li>\r\n 	<li>Bring just to a boil.</li>\r\n 	<li>Immediately pour the boiling cream over and the chocolate and allow it to stand for 5 minutes.</li>\r\n 	<li>Stir with a whisk until smooth.</li>\r\n 	<li>Using a spatula, scoop the ganache into a piping bag with a tip of your choice.</li>\r\n 	<li>Frost each cupcake with the ganache before serving.</li>\r\n</ul>'),
