(8290, 1066, 'integedien', '<ul>\r\n 	<li>3 eggs</li>\r\n 	<li>150g butter</li>\r\n 	<li>225g brown sugar</li>\r\n 	<li>1tsp vanilla essence</li>\r\n 	<li>225g flour</li>\r\n 	<li>1½tsp baking powder</li>\r\n 	<li>3 bananas, mashed</li>\r\n 	<li>150g roasted walnuts, chopped</li>\r\n 	<li>1tsp cinnamon powder</li>\r\n</ul>'),
(8291, 1066, '_integedien', 'field_597ed2046dfdf'),
(8292, 1066, 'method', '<ul>\r\n 	<li>Whisk together the butter, brown sugar and vanilla essence until thick.</li>\r\n 	<li>Add eggs one at a time, whisking well after each addition.</li>\r\n 	<li>Mix in the mashed bananas and cinnamon powder.</li>\r\n 	<li>Sift together the flour and baking powder and fold it into the batter.</li>\r\n 	<li>Add the chopped roasted walnuts and mix.</li>\r\n 	<li>Pour into a lined loaf tin and bake at 165°C for 20 to 25 minutes.</li>\r\n</ul>'),
