(7306, 985, 'integedien', '<ul>\r\n 	<li>170g butter</li>\r\n 	<li>150g dark chocolate</li>\r\n 	<li>85g white chocolate</li>\r\n 	<li>15g cocoa powder</li>\r\n 	<li>350g castor sugar</li>\r\n 	<li>3 eggs</li>\r\n 	<li>70g flour</li>\r\n 	<li>1/4 tsp baking powder</li>\r\n</ul>'),
(7307, 985, '_integedien', 'field_597ed2046dfdf'),
(7308, 985, 'method', '<ul>\r\n 	<li>Preheat the oven to 160°C</li>\r\n 	<li>Sift together the flour and baking powder.</li>\r\n 	<li>Over a hot water bath, melt the butter, dark chocolate and white chocolate.</li>\r\n 	<li>Mix with the cocoa powder and set aside to cool.</li>\r\n 	<li>Whisk the castor sugar and eggs until light and fluffy.</li>\r\n 	<li>Slowly pour the melted chocolate mixture over the eggs and whisk well.</li>\r\n 	<li>Be careful not to over mix.</li>\r\n 	<li>Fold in the flour.</li>\r\n 	<li>Spread the batter in a greased 9-inch baking tray.</li>\r\n 	<li>Bake for 30 minutes or until a skewer comes out clean.</li>\r\n 	<li>Once the brownies have cooled down a little, cut to the desired size.</li>\r\n</ul>'),
