(7431, 1006, 'integedien', '<ul>\r\n 	<li>110g butter</li>\r\n 	<li>200g castor sugar</li>\r\n 	<li>1tsp vanilla essence</li>\r\n 	<li>2 eggs</li>\r\n 	<li>150g flour</li>\r\n 	<li>1tsp baking powder</li>\r\n 	<li>80ml milk</li>\r\n</ul>'),
(7432, 1006, '_integedien', 'field_597ed2046dfdf'),
(7433, 1006, 'method', '<ul>\r\n 	<li>Preheat the oven to 175°C</li>\r\n 	<li>In a bowl, beat the butter, castor sugar and vanilla essence until light and fluffy.</li>\r\n 	<li>Add the eggs one at a time, whisking after each addition until mixed well.</li>\r\n 	<li>Sift together the flour and baking powder.</li>\r\n 	<li>Add the milk and mix with a spatula.</li>\r\n 	<li>Then fold in the flour.</li>\r\n 	<li>Pour the batter into the pan till 3/4th full.</li>\r\n 	<li>Bake for 15 minutes or until a skewer when inserted in the center of the cake comes out clean.</li>\r\n</ul>'),
