(7469, 1012, 'integedien', '<ul>\r\n 	<li>200g butter</li>\r\n 	<li>125g castor sugar</li>\r\n 	<li>1tsp vanilla essence</li>\r\n 	<li>250g flour</li>\r\n</ul>'),
(7470, 1012, '_integedien', 'field_597ed2046dfdf'),
(7471, 1012, 'method', '<ul>\r\n 	<li>In a bowl, whisk together the butter, castor sugar and vanilla essence till soft and fluffy.</li>\r\n 	<li>Fold in the flour with a spatula.</li>\r\n 	<li>Wrap the dough in cling film and refrigerate for a minimum of two hours.</li>\r\n 	<li>Preheat the oven to 165°C and either grease your baking tray with butter or line it with parchment paper.</li>\r\n 	<li>Roll the dough out to approximately 0.5cm thickness.</li>\r\n 	<li>Cut into shapes using the cookie cutter.</li>\r\n 	<li>Place cookies 1 inch apart on the tray and bake for 15 minutes or till the edges start browning.</li>\r\n</ul>'),
